import { useState, useEffect, useRef } from "react";
import { apiService } from "../services/api";

const Profile = () => {
    const [userData, setUserData] = useState(null);
    const [loading, setLoading] = useState(true);
    const [uploading, setUploading] = useState(false);
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');
    const fileInputRef = useRef(null);

    useEffect(() => {
        fetchUserProfile();
    }, []);

    // Helper function to format profile picture URL
    const formatProfilePictureUrl = (url) => {
        if (!url) return '/default-profile.png';

        console.log('Original URL from backend:', url); // Debug log

        // If it's already a full URL or data URL, use it as is
        if (url.startsWith('http') || url.startsWith('data:') || url.startsWith('/')) {
            return url;
        }

        // If it's just a filename like "abc123.jpg", prepend the path
        if (!url.includes('/')) {
            return `/profile-picture/${url}`;
        }

        // If it's a path like "profile-picture/abc123.jpg", ensure it starts with /
        if (url.startsWith('profile-picture/')) {
            return `/${url}`;
        }

        // For any other case, try to extract filename and construct path
        const filename = url.split('/').pop();
        return `/profile-picture/${filename}`;
    };

    const fetchUserProfile = async () => {
        setLoading(true);
        setError('');

        try {
            const response = await apiService.getMyProfile();

            if (response.data.statusCode === 200) {
                setUserData(response.data.data);

                // Debug: Check the formatted URL
                if (response.data.data.profilePictureUrl) {
                    console.log('Formatted image URL:', 
                        formatProfilePictureUrl(response.data.data.profilePictureUrl));
                }
            } else {
                setError(response.data.message);
            }

        } catch (error) {
            setError(error.response?.data?.message || 'An error occurred while fetching profile data');
        } finally {
            setLoading(false);
        }
    }

    const uploadProfilePictureFile = async (file) => {
        setUploading(true);
        setError('');
        setSuccess('');

        try {
            const response = await apiService.uploadProfilePicture(file);

            if (response.data.statusCode === 200) {
                setSuccess('Profile picture updated successfully!');
                await fetchUserProfile(); // This will refresh with the new URL

                setTimeout(() => {
                    setSuccess('');
                }, 4000);
            }

        } catch (error) {
            setError(error.response?.data?.message || 'An error occurred while uploading profile picture');
        } finally {
            setUploading(false);
        }
    }

    const handleFileSelect = (event) => {
        const file = event.target.files[0];
        if (file) {
            if (!file.type.startsWith('image/')) {
                setError('Please select an image file');
                return;
            }

            if (file.size > 5 * 1024 * 1024) {
                setError('File size should be less than 5MB');
                return;
            }

            uploadProfilePictureFile(file);
        }
    }

    const triggerFileInput = () => {
        fileInputRef.current?.click();
    };

    // Add image error handling
    const handleImageError = (e) => {
        console.error('Image failed to load:', e.target.src);
        e.target.src = '/default-profile.png';
    };

    if (loading) {
        return (
            <div className="profile-container">
                <div className="loading">Loading your profile information...</div>
            </div>
        );
    }

    if (error && !userData) {
        return (
            <div className="profile-container">
                <div className="error-message">{error}</div>
            </div>
        );
    }

    if (!userData) {
        return (
            <div className="profile-container">
                <div className="error-message">No profile data available</div>
            </div>
        );
    }

    return (
        <div className="profile-container">
            <div className="profile-header">
                <h1>My Profile</h1>
                <a href="/update-profile" className="btn btn-primary">Change Password</a>
            </div>

            {error && <div className="error-message">{error}</div>}
            {success && <div className="success-message">{success}</div>}

            <div className="profile-content">
                <div className="profile-picture-section">
                    <h2>Profile Picture</h2>
                    <div className="profile-picture-upload">
                        <div className="profile-picture">
                            <img
                                src={formatProfilePictureUrl(userData.profilePictureUrl)}
                                alt="Profile"
                                onError={handleImageError}
                            />
                        </div>

                        <div className="upload-controls">
                            <input
                                type="file"
                                ref={fileInputRef}
                                onChange={handleFileSelect}
                                accept="image/*"
                                style={{ display: 'none' }}
                            />

                            <button
                                onClick={triggerFileInput}
                                className="btn btn-primary"
                                disabled={uploading}
                            >
                                {uploading ? 'Uploading...' : 'Change Picture'}
                            </button>

                        </div>

                        <p className="upload-note">
                            Supported formats: JPG, PNG, GIF. Max size: 5MB
                        </p>
                    </div>
                </div>

                {/* Rest of your component remains the same */}
                <div className="profile-info">
                    <h2>Personal Information</h2>
                    <div className="info-grid">
                        <div className="info-item">
                            <label>First Name</label>
                            <p>{userData.firstname || 'Not available'}</p>
                        </div>
                        <div className="info-item">
                            <label>Last Name</label>
                            <p>{userData.lastname || 'Not available'}</p>
                        </div>
                        <div className="info-item">
                            <label>Email</label>
                            <p>{userData.email || 'Not available'}</p>
                        </div>
                        <div className="info-item">
                            <label>Phone Number</label>
                            <p>{userData.phoneNumber || 'Not available'}</p>
                        </div>
                        <div className="info-item">
                            <label>Status</label>
                            <p className={userData.active ? 'status active' : 'status inactive'}>
                                {userData.active ? 'ACTIVE' : 'INACTIVE'}
                            </p>
                        </div>
                    </div>
                </div>

                {/* Accounts section remains the same */}
                <div className="accounts-section">
                    <h2>My Accounts</h2>
                    {userData.accounts && userData.accounts.length > 0 ? (
                        userData.accounts.map(account => (
                            <div key={account.id} className="account-card">
                                {/* ... account card content ... */}
                            </div>
                        ))
                    ) : (
                        <p>No accounts found</p>
                    )}
                </div>
            </div>
        </div>
    );
}

export default Profile;
